// RegisterForm.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import authService from "../services/authService";
import Modal from "./Modal"; // Import the Modal component

const RegisterForm = () => {
  const { role } = useParams(); // Get role from URL params (client or barber)
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showModal, setShowModal] = useState(false); // Modal state
  const navigate = useNavigate();

  useEffect(() => {
    // Validate role
    if (role !== "client" && role !== "barber") {
      navigate("/"); // Redirect to home if invalid role
    }
  }, [role, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await authService.register(name, email, password, role);
      navigate("/login"); // Redirect to login after successful registration
    } catch (error) {
      console.error("Registration error:", error);
    }
  };

  return (
    <div>
      <button onClick={() => setShowModal(true)}>Open Registration</button>
      <Modal showModal={showModal} closeModal={() => setShowModal(false)}>
        <form onSubmit={handleSubmit}>
          <h2>
            {role === "barber" ? "Register as Barber" : "Register as Client"}
          </h2>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Name"
            required
          />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          <button type="submit">Register</button>
        </form>
      </Modal>
    </div>
  );
};

export default RegisterForm;
